import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { fetchTasksFromAPI } from "../redux/taskslice";

const Home = () => {
  const dispatch = useDispatch();
  const { list, loading, error } = useSelector((state) => state.tasks);

  useEffect(() => {
    dispatch(fetchTasksFromAPI());
  }, [dispatch]);

  if (loading) return <p>Loading tasks...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="container">
      <h1>To-Do List</h1>
      <Link to="/add" className="add-task-btn">
        Add Task
      </Link>
      <ul>
        {list.map((task) => (
          <li key={task.id}>
            <span className="task-title">{task.title}</span>
            <span
              className={`task-status ${task.completed ? "completed" : ""}`}
            >
              {task.completed ? "Completed" : "Pending"}
            </span>
            <Link to={`/edit/${task.id}`} className="edit-btn">
              Edit
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
