import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import { editTask } from "../redux/tasksSlice";

const EditTask = () => {
  const { id } = useParams(); // Get task ID from URL
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Find the task in Redux state
  const task = useSelector((state) =>
    state.tasks.list.find((task) => task.id === parseInt(id))
  );

  // Use local state to edit the task's completed status
  const [completed, setCompleted] = useState(task?.completed || false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Update task locally
    dispatch(editTask({ id: parseInt(id), updatedTask: { completed } }));
    navigate("/"); // Redirect to Home
  };

  if (!task) return <p>Task not found!</p>;

  return (
    <div className="container">
      <h1>Edit Task</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="status">Status:</label>
        <select
          id="status"
          value={completed}
          onChange={(e) => setCompleted(e.target.value === "true")}
        >
          <option value="false">Pending</option>
          <option value="true">Completed</option>
        </select>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditTask;
