import { createAsyncThunk } from "@reduxjs/toolkit";
import { fetchTasks, addTask, editTask } from "../services/api";

// Fetch tasks
export const fetchTasksFromAPI = createAsyncThunk(
  "tasks/fetchTasks",
  async () => {
    return await fetchTasks();
  }
);

// Add a new task
export const addTaskToAPI = createAsyncThunk("tasks/addTask", async (task) => {
  return await addTask(task);
});

// Edit a task
export const editTaskInAPI = createAsyncThunk(
  "tasks/editTask",
  async ({ id, task }) => {
    return await editTask(id, task);
  }
);

